mapper.select:
SELECT
count(*)
FROM exam_problem,problem_info
WHERE problem_info.prob_id = exam_problem.problem_id

    AND
    exam_problem.exam_id = #{record.examId}


    AND
    problem_info.know_id = #{record.knowId}


    AND
    problem_info.level_id = #{record.levelId}


    LIMIT #{start}, #{rows}
