mapper.select:
SELECT

FROM course_info
WHERE 1 = 1

    AND
    cou_id = #{record.couId}


    AND
    course_name = #{record.courseName}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}




    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM course_info
WHERE 1 = 1

    AND
    cou_id = #{record.couId}


    AND
    course_name = #{record.courseName}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}




    LIMIT #{start}, #{rows}

mapper.delete:
delete from course_info
where cou_id in

    #{item}

mapper.select:
select
from course_info where cou_id in
#{item}
