mapper.select:
SELECT

FROM exam_param
WHERE 1 = 1

    AND
    exa_par_id = #{record.exaParId}


    AND
    exam__id = #{record.examId}


    AND
    name = #{record.name}


    AND
    know_id = #{record.knowId}


    AND
    problem_id = #{record.problemId}


    AND
    level_id = #{record.levelId}


    AND
    score = #{record.score}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM exam_param
WHERE 1 = 1

    AND
    exa_par_id = #{record.exaParId}


    AND
    exam__id = #{record.examId}


    AND
    name = #{record.name}


    AND
    know_id = #{record.knowId}


    AND
    problem_id = #{record.problemId}


    AND
    level_id = #{record.levelId}


    AND
    score = #{record.score}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

