mapper.select:
SELECT

FROM judger_info
WHERE 1 = 1

    AND
    jud_id = #{record.judId}


    AND
    name = #{record.name}


    AND
    repr = #{record.repr}


    AND
    comment_prefix = #{record.commentPrefix}


    AND
    suffix = #{record.suffix}


    AND
    enabled = #{record.enabled}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}

ORDER BY jud_id DESC

    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM judger_info
WHERE 1 = 1

    AND
    jud_id = #{record.judId}


    AND
    name = #{record.name}


    AND
    repr = #{record.repr}


    AND
    comment_prefix = #{record.commentPrefix}


    AND
    suffix = #{record.suffix}


    AND
    enabled = #{record.enabled}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}

ORDER BY jud_id DESC

    LIMIT #{start}, #{rows}

mapper.delete:
delete from judger_info
where jud_id in

    #{item}

mapper.select:
select
from judger_info where jud_id in
#{item}
