mapper.select:
SELECT

FROM knowledge_info
WHERE 1 = 1

    AND
    know_id = #{record.knowId}


    AND
    know_name = #{record.knowName}


    AND
    course_id = #{record.courseId}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM knowledge_info
WHERE 1 = 1

    AND
    know_id = #{record.knowId}


    AND
    know_name = #{record.knowName}


    AND
    course_id = #{record.courseId}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

mapper.delete:
delete from knowledge_info
where use_pro_id in

    #{item}
