mapper.select:
SELECT

FROM operate_log
WHERE 1 = 1

    AND
    id = #{record.id}


    AND
    user_id = #{record.userId}


    AND
    ip = #{record.ip}


    AND
    api_url = #{record.apiUrl}


    AND
    api_desc = #{record.apiDesc}


    AND
    api_argc = #{record.apiArgc}


    AND
    exam_id = #{record.examId}


    AND
    problem_id = #{record.problemId}


    AND
    create_time = #{record.createTime}




    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM operate_log
WHERE 1 = 1

    AND
    id = #{record.id}


    AND
    user_id = #{record.userId}


    AND
    ip = #{record.ip}


    AND
    api_url = #{record.apiUrl}


    AND
    api_desc = #{record.apiDesc}


    AND
    api_argc = #{record.apiArgc}


    AND
    exam_id = #{record.examId}


    AND
    problem_id = #{record.problemId}


    AND
    create_time = #{record.createTime}




    LIMIT #{start}, #{rows}

