mapper.select:
SELECT

FROM result_info
WHERE 1 = 1

    AND
    resu_id = #{record.resuId}


    AND
    name = #{record.name}


    AND
    short_name = #{record.shortName}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}




    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM result_info
WHERE 1 = 1

    AND
    resu_id = #{record.resuId}


    AND
    name = #{record.name}


    AND
    short_name = #{record.shortName}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}




    LIMIT #{start}, #{rows}

