mapper.select:
SELECT

,

FROM site_info
WHERE 1 = 1

    AND
    site_id = #{record.siteId}


    AND
    name = #{record.name}


    AND
    type = #{record.type}


    AND
    display = #{record.display}


    AND
    extra = #{record.extra}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}


    AND
    value = #{record.value}




    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM site_info
WHERE 1 = 1

    AND
    site_id = #{record.siteId}


    AND
    name = #{record.name}


    AND
    type = #{record.type}


    AND
    display = #{record.display}


    AND
    extra = #{record.extra}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}


    AND
    value = #{record.value}




    LIMIT #{start}, #{rows}

mapper.delete:
delete from site_info
