mapper.select:
SELECT

FROM submit_similarity
WHERE 1 = 1

    AND
    sub_sim_id = #{record.subSimId}


    AND
    exam_id = #{record.examId}


    AND
    submit_id1 = #{record.submitId1}


    AND
    submit_id2 = #{record.submitId2}


    AND
    similarity = #{record.similarity}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}

ORDER BY sub_sim_id DESC

    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM submit_similarity
WHERE 1 = 1

    AND
    sub_sim_id = #{record.subSimId}


    AND
    exam_id = #{record.examId}


    AND
    submit_id1 = #{record.submitId1}


    AND
    submit_id2 = #{record.submitId2}


    AND
    similarity = #{record.similarity}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}

ORDER BY sub_sim_id DESC

    LIMIT #{start}, #{rows}

mapper.delete:
delete from submit_similarity
where sub_sim_id in

    #{item}

mapper.select:
select
from submit_similarity where sub_sim_id in
#{item}
