mapper.select:
SELECT

FROM user_login_log
WHERE 1 = 1

    AND
    use_log_id = #{record.useLogId}


    AND
    user_id = #{record.userId}


    AND
    login_ip = #{record.loginIp}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM user_login_log
WHERE 1 = 1

    AND
    use_log_id = #{record.useLogId}


    AND
    user_id = #{record.userId}


    AND
    login_ip = #{record.loginIp}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

