mapper.select:
SELECT

FROM user_profile
WHERE 1 = 1

    AND
    use_pro_id = #{record.useProId}


    AND
    real_name = #{record.realName}


    AND
    is_student = #{record.isStudent}


    AND
    class_name = #{record.className}


    AND
    student_number = #{record.studentNumber}


    AND
    entrance_year = #{record.entranceYear}


    AND
    major_id = #{record.majorId}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM user_profile
WHERE 1 = 1

    AND
    use_pro_id = #{record.useProId}


    AND
    real_name = #{record.realName}


    AND
    is_student = #{record.isStudent}


    AND
    class_name = #{record.className}


    AND
    student_number = #{record.studentNumber}


    AND
    entrance_year = #{record.entranceYear}


    AND
    major_id = #{record.majorId}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    LIMIT #{start}, #{rows}

mapper.delete:
delete from user_profile
where use_pro_id in

    #{item}

mapper.select:
select
from user_profile where use_pro_id in
#{item}
