mapper.select:
SELECT

FROM visit_path
WHERE 1 = 1

    AND
    vis_pat_id = #{record.visPatId}


    AND
    url = #{record.url}


    AND
    description = #{record.description}


    AND
    role_ids = #{record.roleIds}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}




    LIMIT #{start}, #{rows}

SELECT
count(*)
FROM visit_path
WHERE 1 = 1

    AND
    vis_pat_id = #{record.visPatId}


    AND
    url = #{record.url}


    AND
    description = #{record.description}


    AND
    role_ids = #{record.roleIds}


    AND
    create_time = #{record.createTime}


    AND
    modify_time = #{record.modifyTime}


    AND
    modify_user_id = #{record.modifyUserId}




    LIMIT #{start}, #{rows}

mapper.delete:
delete from visit_path
where vis_pat_id in

#{item}

